# assign a suitable value for each of these environment # variables that change the way the elements behave. export HOST_USERNAME export HOST_SCP_USERNAME export GUEST_USERNAME export CONTROLLER_IP export TROVESTACK_SCRIPTS export SERVICE_TYPE export PATH_TROVE export ESCAPED_PATH_TROVE export SSH_DIR export GUEST_LOGDIR export ESCAPED_GUEST_LOGDIR export DIB_CLOUD_INIT_DATASOURCES="ConfigDrive" export DATASTORE_PKG_LOCATION export BRANCH_OVERRIDE
# you typically do not have to change these variables export ELEMENTS_PATH=$TROVESTACK_SCRIPTS/files/elements export ELEMENTS_PATH+=:$PATH_DISKIMAGEBUILDER/elements export ELEMENTS_PATH+=:$PATH_TRIPLEO_ELEMENTS/elements export DIB_APT_CONF_DIR=/etc/apt/apt.conf.d export DIB_CLOUD_INIT_ETC_HOSTS=true local QEMU_IMG_OPTIONS=$(! $(qemu-img | grep -q 'version 1') && echo "--qemu-img-options compat=0.10")
# run disk-image-create that actually causes the image to be built ${PATH_DISKIMAGEBUILDER}/bin/disk-image-create -a amd64 -o "${VM}" \ -x ${QEMU_IMG_OPTIONS} ${DISTRO} ${EXTRA_ELEMENTS} vm \ cloud-init-datasources ${DISTRO}-guest ${DISTRO}-${SERVICE_TYPE}

