sidebar.para:
This tutorial demonstrates how to integrate the &kw-hos-phrase;
                centralized logging solution with Splunk, including the steps to set up and forward
                logs.

listitem.screen:
# Logstash outputs
#----------------------------------------------------------------------------------------
output {
# Configure Elasticsearch output
# http://www.elastic.co/guide/en/logstash/current/plugins-outputs-elasticsearch.html
elasticsearch {
hosts => ["{{ elasticsearch_http_host }}:{{ elasticsearch_http_port }}"]
flush_size => 5000
idle_flush_time => 5
workers => {{ logstash_num_workers }}
    }
         # Forward Logs to Splunk on TCP port 5514
            tcp {
            mode => "client"
            host => "<Enter Splunk listener IP address>"
            port => 5514
            }
}
