sect2.screen:
def print_values(val, type):
    if type == 'ports':
        val_list = val['ports']
    if type == 'networks':
        val_list = val['networks']
    if type == 'routers':
        val_list = val['routers']
    for p in val_list:
        for k, v in p.items():
            print("%s : %s" % (k, v))
        print('\n')


def print_values_server(val, server_id, type):
    if type == 'ports':
        val_list = val['ports']

    if type == 'networks':
        val_list = val['networks']
    for p in val_list:
        bool = False
        for k, v in p.items():
            if k == 'device_id' and v == server_id:
                bool = True
        if bool:
            for k, v in p.items():
                print("%s : %s" % (k, v))
            print('\n')

#!/usr/bin/env python from neutronclient.v2_0 import client from credentials import get_credentials
network_name = 'sample_network' credentials = get_credentials() neutron = client.Client(**credentials) try: body_sample = {'network': {'name': network_name, 'admin_state_up': True}}
netw = neutron.create_network(body=body_sample) net_dict = netw['network'] network_id = net_dict['id'] print('Network %s created' % network_id)
body_create_subnet = {'subnets': [{'cidr': '192.168.199.0/24', 'ip_version': 4, 'network_id': network_id}]}
subnet = neutron.create_subnet(body=body_create_subnet) print('Created subnet %s' % subnet) finally: print("Execution completed")

#!/usr/bin/env python from neutronclient.v2_0 import client from credentials import get_credentials from utils import print_values
credentials = get_credentials() neutron = client.Client(**credentials) netw = neutron.list_networks()
print_values(netw, 'networks')

#!/usr/bin/env python from neutronclient.v2_0 import client import novaclient.v1_1.client as nvclient from credentials import get_credentials from credentials import get_nova_credentials
credentials = get_nova_credentials() nova_client = nvclient.Client(**credentials)
# Replace with server_id and network_id from your environment
server_id = '9a52795a-a70d-49a8-a5d0-5b38d78bd12d' network_id = 'ce5d204a-93f5-43ef-bd89-3ab99ad09a9a' server_detail = nova_client.servers.get(server_id) print(server_detail.id)
if server_detail != None: credentials = get_credentials() neutron = client.Client(**credentials)
body_value = { "port": { "admin_state_up": True, "device_id": server_id, "name": "port1", "network_id": network_id } } response = neutron.create_port(body=body_value) print(response)

#!/usr/bin/env python from neutronclient.v2_0 import client import novaclient.v1_1.client as nvclient from credentials import get_credentials from credentials import get_nova_credentials from utils import print_values_server
credentials = get_nova_credentials() nova_client = nvclient.Client(**credentials)
# change these values according to your environment
server_id = '9a52795a-a70d-49a8-a5d0-5b38d78bd12d' network_id = 'ce5d204a-93f5-43ef-bd89-3ab99ad09a9a' server_detail = nova_client.servers.get(server_id) print(server_detail.id)
if server_detail is not None: credentials = get_credentials() neutron = client.Client(**credentials) ports = neutron.list_ports()
print_values_server(ports, server_id, 'ports') body_value = {'port': { 'admin_state_up': True, 'device_id': server_id, 'name': 'port1', 'network_id': network_id, }}
response = neutron.create_port(body=body_value) print(response)

