# Replace with network_id from your environment
network_id = '81bf592a-9e3f-4f84-a839-ae87df188dc1' try: credentials = get_credentials() neutron = client.Client(**credentials) neutron.format = 'json' request = {'router': {'name': 'router name', 'admin_state_up': True}} router = neutron.create_router(request) router_id = router['router']['id'] # for example: '72cf1682-60a8-4890-b0ed-6bad7d9f5466' router = neutron.show_router(router_id) print(router) body_value = {'port': { 'admin_state_up': True, 'device_id': router_id, 'name': 'port1', 'network_id': network_id, }}
response = neutron.create_port(body=body_value) print(response) finally: print("Execution completed")

step.screen:
body_sample = {'network': {'name': network_name, 'admin_state_up': True}}
netw = neutron.create_network(body=body_sample) net_dict = netw['network'] network_id = net_dict['id'] print('Network %s created' % network_id)
body_create_subnet = {'subnets': [{'cidr': '192.168.199.0/24', 'ip_version': 4, 'network_id': network_id}]}
subnet = neutron.create_subnet(body=body_create_subnet) print('Created subnet %s' % subnet)
neutron.delete_network(network_id) print('Deleted Network %s' % network_id)
print("Execution completed")

