#!/usr/bin/env python import time from credentials import get_nova_credentials_v2 from novaclient.client import Client
try: credentials = get_nova_credentials_v2() nova_client = Client(**credentials)
image = nova_client.images.find(name="cirros") flavor = nova_client.flavors.find(name="m1.tiny") net = nova_client.networks.find(label="private") nics = [{'net-id': net.id}] instance = nova_client.servers.create(name="vm2", image=image, flavor=flavor, key_name="keypair-1", nics=nics) print("Sleeping for 5s after create command") time.sleep(5) print("List of VMs") print(nova_client.servers.list()) finally: print("Execution Completed")

#!/usr/bin/env python from credentials import get_nova_credentials_v2 from novaclient.client import Client
credentials = get_nova_credentials_v2() nova_client = Client(**credentials)
servers_list = nova_client.servers.list() server_del = "vm1" server_exists = False
for s in servers_list: if s.name == server_del: print("This server %s exists" % server_del) server_exists = True break if not server_exists: print("server %s does not exist" % server_del) else: print("deleting server..........") nova_client.servers.delete(s) print("server %s deleted" % server_del)

step.screen:
def print_flavors(flavor_list):
    for flavor in flavor_list:
       print("-"*35)
       print("flavor id : %s" % flavor.id)
       print("flavor name : %s" % flavor.name)
       print("-"*35)

